import React, { Component } from "react";

export default class Toggler extends Component {
  state = {
    isOpen: false,
  };

  toggle = () => this.setState((state) => ({ isOpen: !state.isOpen }));

  render() {
    return (
      <div>
        <h1>Hello!</h1>
      </div>
    );
  }
}
