import React, { Component } from "react";

export default class Toggler extends Component {
  state = {
    isOpen: false,
  };

  toggle = () => this.setState((state) => ({ isOpen: !state.isOpen }));

  render() {
    return this.props.render({
      isOpen: this.state.isOpen,
      toggle: this.toggle,
    });
  }
}
