import React, { Component, createContext } from "react";

const Context = createContext();

export default class ThemeProvider extends Component {
  state = {
    type: "light",
    themeConfig: {
      light: {
        headerBg: "#F7B30C",
        fontColor: "black",
        bodybg: "white",
      },
      dark: {
        headerBg: "#3c3c3c",
        fontColor: "white",
        bodybg: "black",
      },
    },
    toggleTheme: this.toggleTheme,
  };

  render() {
    return <div>body</div>;
  }
}
