import { Component } from "react";

export default class Toggler extends Component {
  state = {
    isOpen: false,
  };

  onToggle = () => this.setState((state) => ({ isOpen: !state.isOpen }));

  //   render() {
  //     return this.props.render({
  //       isOpen: this.state.isOpen,
  //       toggle: this.toggle,
  //     });
  //   }
  render() {
    return this.props.children({
      isOpen: this.state.isOpen,
      toggle: this.toggle,
    });
  }
}
